sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


	//Debug message
	//alert('CEP On Change Triggered!');
	//return;

	if (isLoading || newValue === '') {
		return;
	}

	//Storing the CEP as inputed in a variable and uses a 
	//regular expression to replace all non digit characters ˆ\D (non-digit) with a modifier /g (search the entire string field)
	var cepFilial = g_form.getValue('cep');

	var cepFilialOnlyDigits = cepFilial.replace(/[ˆ\D]/g, '');

	//Debug message
	//alert('CEP Numérico: ' + cepFilialOnlyDigits);

	//Validade the cep again basically with a new RegEx  pattern to confirm the size (8), but validates again only numbers [ˆ0-9]
	var regularExpressionToValidateCepFormat = /[ˆ0-9]{8}$/;

	if (regularExpressionToValidateCepFormat.test(cepFilialOnlyDigits)) {


		var restMsgAjax = new GlideAjax('RESTMessageClientCallable');

		//HardCoded because I know here in this form I need to use this Rest, but all can be refactored to be more generic and reusable
		var nameCustomRESTRecCreated = 'Fetch_Address_Correios_API_by_CEP';
		var httpMethod = 'get';
		var format = 'json';
		//Related to the 'Fetch_Address_Correios_API_by_CEP' REST Record Created in Outbound REST module
		var restFunction = 'Default GET';
		var erroMsg;

		restMsgAjax.addParam('sysparam_nameRest', nameCustomRESTRecCreated);
		restMsgAjax.addParam('sysparam_httpMethod', httpMethod);
		restMsgAjax.addParam('sysparam_returnFormat', format);
		restMsgAjax.addParam('sysparam_searchParamValue', cepFilialOnlyDigits);
		restMsgAjax.addParam('sysparam_restFunction', restFunction);

		//Function to be execute on the Server
		restMsgAjax.addParam('sysparm_name','executeCall');

		try {
			//Invocação assícrona com determinação da função de callback
			restMsgAjax.getXMLAnswer(apiCallBackAnswer);
		} catch (ex) {
			erroMsg = "Ocorreu um erro ao chamar a API https://viacep.com.br, favor verificar se a mesma está okay. Erro:" + ex.message;
			alert(erroMsg);
		}		

		//Debug message
		//alert('O CEP está no formato certo!');

	} else {
		alert('CEP inválido, por favor verifique');
	}

	//Call back function
	function apiCallBackAnswer(answer) {

		//alert(answer);

		var parsedAddressData = JSON.parse(answer);

		//alert(parsedAddressData.logradouro);

		g_form.setValue('cep', parsedAddressData.cep);
		g_form.setValue('endereco', parsedAddressData.logradouro);
		g_form.setValue('bairro', parsedAddressData.bairro);
		g_form.setValue('complemento', parsedAddressData.complemento);
		g_form.setValue('cidade', parsedAddressData.localidade);
		g_form.setValue('estado', parsedAddressData.uf);

	}	

	return;		

}
